from ..uri import parse
from . import dispatch


def download(MODEL_URI: str, dirpath: str) -> None:
    request_type = parse.request_type(MODEL_URI)
    dispatch.REQUEST_HANDLERS[request_type](MODEL_URI, dirpath)
